mapper.select:
        
        SELECT MEMBERID
        FROM MEMBERS
        WHERE MEMBERID = #{memberId}

        
        SELECT MEMBERID
        FROM MEMBERS
        WHERE MEMBERID = #{memberId} and MEMBERPWD = #{memberPwd}

        
        SELECT MEMBERID
        FROM MEMBERS
        WHERE MEMBERID = #{memberId} and MEMBERPWD = #{memberPwd}
